import React, { useState } from "react";
import "./components/Expenses/Expenses.css";
import Expenses from "./components/Expenses/Expenses.js";
import NewExpense from "./components/NewExpense/NewExpense";

const DUMMY_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const App = () => {
  const [expenses, setExpenses] = useState(DUMMY_EXPENSES);

  const addExpenseHandler = (expense) => {
    // console.log("In App.js");
    // console.log(expenses);
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  };

  const filterExpenseHandler = (selectedYear) => {
    console.log("date" + JSON.stringify(expenses[0].date.getFullYear));
    // const filterResult = expenses.filter(
    //   (expense) => expense.date.year === selectedYear
    // );
    // console.log("filterExpenseHandler" + filterResult.length);
    // setExpenses(filterResult);
  };
  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      <Expenses expenses={expenses} onChangeExpense={filterExpenseHandler} />
    </div>
  );
};

export default App;
